    const greenfield_url = 'https://backendinvest.admlr.lipetsk.ru/greenfield/?format=json'
    fetch(url, {
      method: "GET"
    })
      .then(response => response.json())
      .then(data => (this.info = data));
 
    fetch(url_region)
